package com.example.pemilahkopi.adapterimport android.annotation.SuppressLintimport android.os.Buildimport android.util.Logimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.annotation.RequiresApiimport androidx.recyclerview.widget.RecyclerViewimport com.example.pemilahkopi.databinding.ItemTableBindingimport com.example.pemilahkopi.model.RiwayatItemimport java.text.SimpleDateFormatimport java.time.LocalDateTimeimport java.time.format.DateTimeFormatterimport java.util.*@RequiresApi(Build.VERSION_CODES.O)class TableAdapter : RecyclerView.Adapter<TableAdapter.TableViewHolder>() {    private var listData = listOf<RiwayatItem>()    fun addData(list: List<RiwayatItem>){        listData = list    }    init {        Log.d("NEW", listData.toString())    }    inner class TableViewHolder(private val binding: ItemTableBinding) :        RecyclerView.ViewHolder(binding.root) {        @SuppressLint("SimpleDateFormat")        fun bind(item: RiwayatItem) {            binding.apply {                itemId.text = item.id.toString()                tvJenisKopi.text = item.kopi                tvBeratKopi.text = item.berat.toString()                val dateFormat = SimpleDateFormat("dd-MM-yyyy HH:mm", Locale("in", "ID"))                val newTime = dateFormat.format(Date(item.created.toString().toLong() * 1000))                tvWaktu.text = newTime.toString()            }            Log.d("DATA", listData.toString())        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): TableViewHolder {        return TableViewHolder(ItemTableBinding.inflate(LayoutInflater.from(parent.context),            parent,            false))    }    override fun onBindViewHolder(holder: TableViewHolder, position: Int) {        holder.bind(listData[position])    }    override fun getItemCount(): Int {        return listData.size    }}