package com.example.pemilahkopi.networkimport com.example.pemilahkopi.model.ControlMesinResponseimport com.example.pemilahkopi.model.DataBeratResponseimport com.example.pemilahkopi.model.DataKopiResponseimport com.example.pemilahkopi.model.ResetKopiResponseimport okhttp3.ResponseBodyimport retrofit2.Callimport retrofit2.http.*interface ApiService {    @GET("public/api/riwayat")    fun getDataKopi(): Call<DataKopiResponse>    @FormUrlEncoded    @POST("public/api/mesin")    fun setControlMesin(        @Field("status") state: Int,    ): Call<ControlMesinResponse>    @GET("public/api/berat")    fun getDataBerat(): Call<DataBeratResponse>    @POST("public/api/reset")    fun resetDataKopi() : Call<ResetKopiResponse>}